import React , {useEffect, useState} from 'react'
import {Link} from 'react-router-dom'
import './SubCategory.css'

function SubCategory(props) {

    const [TravelData, setTravelData] = useState({})
    const [CategoryName, setCategoryName] = useState(null)

    useEffect(
        () => {
            // on component mount 
            if (props.location.myparams) {
                console.log(props.location.myparams)
                console.log(props.match.params.category)
                setTravelData(props.location.myparams)
                setCategoryName(props.match.params.category)
            }


            return () => {
                // component unmount 

            }
        },

        [])

    return (
        <div className='subCategoryRoot'>
            <div className='subCategoryName'>
                { CategoryName ? <p> {CategoryName} </p> : <p> no data receieved from server </p>}
            </div>
            <div className='categoryItems'>
                {TravelData.PlacesArray.map(Place => (
                    <div className='categoryItem' 
                        key={Place.id}
                    >
                        <img src={Place.CoverImage} />
                        <Link className='categoryItemName' to= {Place.Link} >  
                            <p> {Place.PlaceName} </p>
                        </Link> 
                    </div>  
                ))}
            </div>
        </div>
    )
}

export default SubCategory
