import React, {useState,useEffect,useRef} from 'react'
import './Gallery.css'

function Gallery(props) {

    const albumRef = useRef(null)

    const [AlbumList, setAlbumList] = useState(
                                                [
                                                    {
                                                        id        : 1,
                                                        AlbumName : "Travel",
                                                        CoverImage: "Gallery/Travel/Travel.jpg"
                                                    },
                                                    {
                                                        id        : 2,
                                                        AlbumName : "Animals",
                                                        CoverImage: "Gallery/Animals/Animals.jpg"
                                                    },
                                                    {
                                                        id        : 3,
                                                        AlbumName : "Nature",
                                                        CoverImage: "Gallery/Nature/Nature.jpg"
                                                    },
                                                    {
                                                        id        : 4,
                                                        AlbumName : "People",
                                                        CoverImage: "Gallery/People/People.jpg"
                                                    },
                                                    {
                                                        id        : 5,
                                                        AlbumName : "Sports",
                                                        CoverImage: "Gallery/Sports/Sports.jpg"
                                                    },
                                                    {
                                                        id        : 6,
                                                        AlbumName : "Portrait",
                                                        CoverImage: "Gallery/Portrait/Portrait.jpg"
                                                    },
                                                    {
                                                        id        : 7,
                                                        AlbumName : "Architecture",
                                                        CoverImage: "Gallery/Architecture/Architecture.jpg"
                                                    },
                                                ]
                                            )

    const [scrollBarPositionGetter, scrollBarPositionSetter] = useState(0);

    useEffect(
        (scrollBarPositionGetter) => {
            updateElementScrollPosition(albumRef,scrollBarPositionGetter)
        },

        [scrollBarPositionGetter])

    // function handleOnWheel (e) {
    //     console.log ("hello onWhile")
    // }       

    const handleOnWheel = (e) => {

        // e.preventDefault();

        let scrollBarPosition ;

        console.log(e)

        if (e.deltaY < 0){
            console.log('scroll up')
            scrollBarPosition =  scrollBarPositionGetter - 0.01;
            if(scrollBarPosition <= 0) {
                scrollBarPosition = 0 ;
            }
            scrollBarPositionSetter(scrollBarPosition);

        } else if (e.deltaY > 0){

            console.log('scroll down')

            scrollBarPosition =  scrollBarPositionGetter + 0.01;
            if(scrollBarPosition >= 1 ) {
                scrollBarPosition = 1 ;
            }
            scrollBarPositionSetter(scrollBarPosition);

        }

        console.log("scrollBarPositionGetter: ",scrollBarPositionGetter)

    }


    const updateElementScrollPosition = (element,scrollBarPosition) => {

        // element.current.scrollLeft = 300
        console.log(element.current.scrollWidth)
        element.current.scrollLeft = element.current.scrollWidth * scrollBarPosition
        console.log(" scrollleft :  ",element.current.scrollLeft)
    }

    const addEventListener2Component = () => {
        albumRef.current.addEventListener('wheel', handleOnWheel)
    }

    const removeEventListenerFromComponent = () => {
        if (albumRef.current) {
            albumRef.current.removeEventListener('wheel',handleOnWheel)
        }
    }

    useEffect(
        () => {
            // on component mount 
            addEventListener2Component();

            return () => {
                // component unmount 
                removeEventListenerFromComponent();
            }
        },

        [])


    return (
        <div className='galleryRoot' 
            onWheel={handleOnWheel}
            ref={albumRef}
        >
            {AlbumList.map(Album => (
                <div className='album' 
                    key={Album.id}
                >
                    {/* <img src={Album.CoverImage} /> */}
                    <p className='albumName'> {Album.AlbumName} l al a lalal al ala la la la l a lla la lal alal al ala la lal </p>
                    <p className='clickForMore'> MORE PHOTOS </p>
                 </div>   
            ))}
        </div>
    )
}

export default Gallery
