import React, {useState,useEffect,useRef} from 'react'
import './Gallery.css'

function Gallery(props) {

    const albumRef = useRef(null)
    const [rootIdRef, setrootIdRef] = useState(null)

    let state = {
        scrollBarPosition : 0 ,
    }

    const [AlbumList, setAlbumList] = useState(
                                                [
                                                    {
                                                        id        : 1,
                                                        AlbumName : "Travel",
                                                        CoverImage: "Gallery/Travel/Travel.jpg"
                                                    },
                                                    {
                                                        id        : 2,
                                                        AlbumName : "Animals",
                                                        CoverImage: "Gallery/Animals/Animals.jpg"
                                                    },
                                                    {
                                                        id        : 3,
                                                        AlbumName : "Nature",
                                                        CoverImage: "Gallery/Nature/Nature.jpg"
                                                    },
                                                    {
                                                        id        : 4,
                                                        AlbumName : "People",
                                                        CoverImage: "Gallery/People/People.jpg"
                                                    },
                                                    {
                                                        id        : 5,
                                                        AlbumName : "Sports",
                                                        CoverImage: "Gallery/Sports/Sports.jpg"
                                                    },
                                                    {
                                                        id        : 6,
                                                        AlbumName : "Portrait",
                                                        CoverImage: "Gallery/Portrait/Portrait.jpg"
                                                    },
                                                    {
                                                        id        : 7,
                                                        AlbumName : "Architecture",
                                                        CoverImage: "Gallery/Architecture/Architecture.jpg"
                                                    },
                                                ]
                                            )


    function handleOnWheel(e){


        if(albumRef.current.scrollLeft >= 0 && albumRef.current.scrollLeft < albumRef.current.scrollWidth) {
            e.preventDefault(); // no scroll on parent 
            albumRef.current.scrollLeft += e.deltaY ;
            if (albumRef.current.scrollLeft < 0)  {
                albumRef.current.scrollLeft = 0;
            } else if (albumRef.current.scrollLeft > albumRef.current.scrollWidth) {
                albumRef.current.scrollLeft = albumRef.current.scrollWidth 
            }
        } 

    }

    const addEventListener2Component = (e) => {
        albumRef.current.addEventListener('wheel', handleOnWheel)
    }

    const removeEventListenerFromComponent = () => {
        if (albumRef.current) {
            albumRef.current.removeEventListener('wheel',handleOnWheel)
        }
    }

    useEffect(
        () => {
            // on component mount 
            addEventListener2Component();

            // document.getElementById('galleryRoot').addEventListener('wheel',handleOnWheel)
            setrootIdRef(document.getElementById('root')) 

            console.log(rootIdRef)

            return () => {
                // component unmount 
                removeEventListenerFromComponent();
                // if(document.getElementById('galleryRoot')){
                //     document.getElementById('galleryRoot').removeEventListener('wheel', handleOnWheel)
                // }
            }
        },

        [])


    return (
        <div className='galleryRoot' 
            ref={albumRef}
        >
            {AlbumList.map(Album => (
                <div className='album' 
                    key={Album.id}
                >
                    <div className='imageWrapper'>
                        {/* <img src={Album.CoverImage} /> */}
                    </div>
                    {/* <p className='albumName'> {Album.AlbumName} l al a lalal al ala la la la l a lla la lal alal al ala la lal </p>
                    <p className='clickForMore'> MORE PHOTOS </p> */}
                 </div>   
            ))}
        </div>
    )
}

export default Gallery
